<div data-role="page" data-theme="d" id="page-redirect">

	{vb:raw navbar}

	<div id="pagetitle" class="pagetitle ui-bar-b">
		<h1 class="pagetitle">{vb:rawphrase redirecting}</h1>
	</div>

	<div data-role="content">
		<form class="block vbform" method="post" action="{vb:raw formfile}" name="postvarform" id="postvarform">
			<p class="blockrow restore">{vb:raw errormessage}</p>
			<vb:if condition="$postvars">
				{vb:raw postvars}
				<p class="blockrow" id="click_here_p">{vb:rawphrase click_the_following_button}</p>
			</vb:if>
			<vb:if condition="$postvars">
				<input type="submit" class="button" id="proceed_button" value="{vb:rawphrase proceed}" accesskey="s" tabindex="1" />
			<vb:else />
				<a href="{vb:raw url}" class="textcontrol">{vb:rawphrase click_if_browser_does_not_redirect}</a>
			</vb:if>
		</form>
	</div>

	<vb:if condition="$postvars">

	<!-- redirect with POST -->

	<script type="text/javascript">
	<!--
	$('#click_here_p').css('display', 'none');

	myvar = "";
	timeout = 18;
	timerID = 0;

	$('[data-role=page]').live('pagecreate', function(event){
		$('form').die(); // Remove default ajax form behavior
		$('a.ui-btn').unbind().click(
			function ()
			{
				clearTimeout(timerID);
				return true;
			}
		);
	});

	function submit_form()
	{
		window.status = "Redirecting" + myvar;
		myvar = myvar + " .";
		timerID = setTimeout("submit_form();", 100);
		if (timeout > 0)
		{
			timeout -= 1;
		}
		else
		{
			clearTimeout(timerID);
			window.status = "";
			$('#postvarform').submit();
			$('#proceed_button').attr('disabled', 'disabled');
		}
	}

	submit_form();
	//-->
	</script>

	<!-- / redirect with POST -->

